// select all divs and give them purple background

$("div").css("background", "purple");

// select all divs with class "highlight"

$("div.highlight").css("width", "200px");

//select div with id third and give orange border

$("#third").css("border", "3px solid orange");

//select first div ONLY and change its font color to pink

$("div:first-of-type").css("color", "pink");